let findPacketStartIndex windowSize (input: string) =
    windowSize
    + (input.ToCharArray()
       |> Array.toSeq
       |> Seq.windowed windowSize
       |> Seq.findIndex (fun chars ->
           not (
               chars
               |> Array.groupBy (fun c -> c)
               |> Array.exists (fun (_, arr) -> arr.Length > 1)
           )))

let part1 input =
    input
    |> findPacketStartIndex 4
    |> printfn "%A start packet found at %A" input

let part2 input =
    input
    |> findPacketStartIndex 14
    |> printfn "%A start message found at %A" input

let input =
    [ "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
      "bvwbjplbgvbhsrlpgdmjqwftvncz"
      "nppdvjthqldpwncqszvftbrmjlhg"
      "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
      "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
      "" ]

input |> List.iter part1
input |> List.iter part2
